<?phpinclude('config.php');// Validasi dan cek apakah parameter `type` dan `id` adaif (!isset($_GET['type']) || !isset($_GET['id'])) {    echo "<script>alert('Parameter tidak lengkap!'); window.location='index.php';</script>";    exit;}// field ID$type = $_GET['type'];$id = (int) $_GET['id']; // Pastikan $id adalah integer// Validasi nilai $typeif ($type !== 'makanan' && $type !== 'minuman') {    echo "<script>alert('Tipe tidak valid!'); window.location='index.php';</script>";    exit;}$table = $type === 'makanan' ? 'tbl_makanan' : 'tbl_minuman';$id_field = $type === 'makanan' ? 'id_makanan' : 'id_minuman';// Ambil data dari database$result = $conn->query("SELECT * FROM $table WHERE $id_field = $id");// Periksa apakah data ditemukanif ($result && $result->num_rows > 0) {    $data = $result->fetch_assoc();} else {    echo "<script>alert('Data tidak ditemukan!'); window.location='index.php';</script>";    exit;}// Proses update dataif (isset($_POST['submit'])) {    $nama = $_POST['nama'];    $daerah = $_POST['daerah'];    $keterangan = $_POST['keterangan'];    if ($type === 'makanan') {        $sql = "UPDATE tbl_makanan SET                 nama_makanan = '$nama',                 daerah_makanan = '$daerah',                 keterangan = '$keterangan'                 WHERE id_makanan = $id";    } else {        $sql = "UPDATE tbl_minuman SET                 nama_minuman = '$nama',                 jenis_minuman = '$daerah',                 keterangan = '$keterangan'                 WHERE id_minuman = $id";    }    if ($conn->query($sql) === TRUE) {        echo "<script>alert('Data berhasil diupdate!'); window.location='index.php';</script>";    } else {        echo "Error: " . $sql . "<br>" . $conn->error;    }}?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Edit Data</title>    <link href="assets/css/bootstrap.min.css" rel="stylesheet"></head><body><div class="container mt-5">    <div class="row justify-content-center">        <div class="col-md-8">            <div class="card">                <div class="card-header bg-primary text-white">                    <h4 class="mb-0 text-center">Edit Data <?php echo ucfirst($type); ?></h4>                </div>                <div class="card-body">                    <form action="" method="POST">                        <div class="mb-3">                            <label for="nama" class="form-label">Nama <?php echo ucfirst($type); ?></label>                            <input type="text" name="nama" class="form-control form-control-lg"                                    value="<?php echo htmlspecialchars($data['nama_' . $type]); ?>" required>                        </div>                        <div class="mb-3">                            <label for="daerah" class="form-label">Daerah / Jenis</label>                            <input type="text" name="daerah" class="form-control form-control-lg"                                    value="<?php echo htmlspecialchars($data[$type === 'makanan' ? 'daerah_makanan' : 'jenis_minuman']); ?>" required>                        </div>                        <div class="mb-3">                            <label for="keterangan" class="form-label">Keterangan</label>                            <textarea name="keterangan" class="form-control form-control-lg" rows="4"><?php echo htmlspecialchars($data['keterangan']); ?></textarea>                        </div>                        <div class="d-flex justify-content-between">                            <a href="index.php" class="btn btn-secondary btn-back">Kembali</a>                            <button type="submit" name="submit" class="btn btn-primary">Update</button>                        </div>                    </form>                </div>            </div>        </div>    </div></div><script src="assets/js/bootstrap.bundle.min.js"></script></body></html>